document.addEventListener('DOMContentLoaded', (event) => {
    let timeout;
  
    function showSVG() {
      document.getElementById('animated-svg').style.display = 'block';
    }
  
    function resetTimeout() {
      clearTimeout(timeout);
      document.getElementById('animated-svg').style.display = 'none';
      timeout = setTimeout(showSVG, 3000);
    }
  
    document.addEventListener('touchstart', resetTimeout);
    document.addEventListener('touchmove', resetTimeout);
    document.addEventListener('touchend', resetTimeout);
    document.addEventListener('touchcancel', resetTimeout);
  });
  